import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import { Navbar as BootstrapNavbar, Nav, Accordion, Card, NavDropdown, Button } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import './css/style.css';

const activeStyle = {
  fontWeight: "bold",
  color: "white",
};

function Navbar() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  return (
    <BootstrapNavbar bg="dark" variant="dark" expand="lg" className="px-4">
      <BootstrapNavbar.Brand as={NavLink} to="/">Navbar</BootstrapNavbar.Brand>
      <BootstrapNavbar.Toggle aria-controls="basic-navbar-nav" />
      <BootstrapNavbar.Collapse id="basic-navbar-nav">
        <Nav className="me-auto">
          <Nav.Item>
            <Nav.Link as={NavLink} exact to="/" activeStyle={activeStyle}>Home</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link as={NavLink} to="/about" activeStyle={activeStyle}>About</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link as={NavLink} to="/contact" activeStyle={activeStyle}>Contact</Nav.Link>
          </Nav.Item>
          <Accordion className="w-100">
            <Card>
              <Accordion.Toggle as={Card.Header} eventKey="0">Dropdown</Accordion.Toggle>
              <Accordion.Collapse eventKey="0">
                <Card.Body>
                  <NavDropdown.Item href="#">Action</NavDropdown.Item>
                  <NavDropdown.Item href="#">Another action</NavDropdown.Item>
                  <NavDropdown.Divider />
                  <NavDropdown.Item href="#">Something else here</NavDropdown.Item>
                </Card.Body>
              </Accordion.Collapse>
            </Card>
          </Accordion>
        </Nav>
        {!isAuthenticated ? (
          <div className="d-flex">
            <Button as={NavLink} to="/login" variant="outline-light" className="me-2">Login</Button>
            <Button as={NavLink} to="/register" variant="outline-light">Register</Button>
          </div>
        ) : (
          <Nav className="ms-auto">
            <form className="d-flex" role="search">
              <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search" />
              <Button variant="outline-success" type="submit">Search</Button>
            </form>
          </Nav>
        )}
      </BootstrapNavbar.Collapse>
    </BootstrapNavbar>
  );
}

export default Navbar;
