import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { Home, About, Contact, ErrorHandling, Login, Register, Profile, EditProfile, PrivacyPolicy, TermsOfUse } from "./pages/index";

import { CookieBanner } from "./components";

function App() {
  const [showCookieConsent, setShowCookieConsent] = useState(false);

  useEffect(() => {
    const hasConsented = localStorage.getItem("cookieConsent");
    setShowCookieConsent(!hasConsented);
  }, []);

  const handleCookieConsent = () => {
    localStorage.setItem("cookieConsent", "true");
    setShowCookieConsent(false);
  };

  return (
    <Router>
      <Switch>
        <Route exact path="/">
          <Home />
        </Route>
        <Route path="/about">
          <About />
        </Route>
        <Route path="/contact">
          <Contact />
        </Route>
        <Route path="/login">
          <Login />
        </Route>
        <Route path="/templates">
          <TemplatePage />
        </Route>
        <Route path="/errors">
          <ErrorHandling />
        </Route>
        <Route path="/register">
          <Register />
        </Route>
        <Route path="/profile">
          <Profile />
        </Route>
        <Route path="/profile/edit">
          <EditProfile />
        </Route>
        <Route path="/terms">
          <TermsOfUse />
        </Route>
        <Route path="/privacy-policy">
          <PrivacyPolicy />
        </Route>
      </Switch>
      {showCookieConsent && <CookieBanner onAccept={handleCookieConsent} />}
    </Router>
  );
}

export default App;
