import React, { useState, useEffect } from "react";
import { Container, Row, Col, Form, Button } from "react-bootstrap";
import Navbar from "../../components/navbar/Navbar";

const EditProfile = () => {
  const [name, setName] = useState("");
  const [title, setTitle] = useState("");
  const [location, setLocation] = useState("");
  const [about, setAbout] = useState("");
  const [skills, setSkills] = useState([]);
  const [education, setEducation] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await fetch("https://localhost:3001/profile");
        const data = await response.json();
        setName(data.name);
        setTitle(data.title);
        setLocation(data.location);
        setAbout(data.about);
        setSkills(data.skills);
        setEducation(data.education);
      } catch (error) {
        console.log(error);
      }
    };

    fetchUser();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("https://localhost:3001/profile", {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          name,
          title,
          location,
          about,
          skills,
          education,
        }),
      });
      const data = await response.json();
      console.log(data);
      setError("");
    } catch (error) {
      console.log(error);
      setError("Unable to update profile");
    }
  };

  return (
    <>
      <Navbar />
      <Container className="my-5">
        <h2>Edit Profile</h2>
        {error && <p>{error}</p>}
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="name">
            <Form.Label>Name</Form.Label>
            <Form.Control type="text" placeholder="Enter name" value={name} onChange={(e) => setName(e.target.value)} />
          </Form.Group>

          <Form.Group controlId="title">
            <Form.Label>Title</Form.Label>
            <Form.Control type="text" placeholder="Enter title" value={title} onChange={(e) => setTitle(e.target.value)} />
          </Form.Group>

          <Form.Group controlId="location">
            <Form.Label>Location</Form.Label>
            <Form.Control type="text" placeholder="Enter location" value={location} onChange={(e) => setLocation(e.target.value)} />
          </Form.Group>

          <Form.Group controlId="about">
            <Form.Label>About</Form.Label>
            <Form.Control as="textarea" rows={3} placeholder="Enter about" value={about} onChange={(e) => setAbout(e.target.value)} />
          </Form.Group>

          <Form.Group controlId="skills">
            <Form.Label>Skills</Form.Label>
            <Form.Control type="text" placeholder="Enter skills (comma separated)" value={skills} onChange={(e) => setSkills(e.target.value.split(","))} />
          </Form.Group>

          <Form.Group controlId="education">
            <Form.Label>Education</Form.Label>
            <Form.Control type="text" placeholder="Enter education" value={education} onChange={(e) => setEducation(e.target.value)} />
          </Form.Group>

          <Button variant="primary" type="submit">
            Save Changes
          </Button>
        </Form>
      </Container>
    </>
  );
};

export default EditProfile;
