import React from "react";

const ProgressBar = ({ progress }) => {
  const progressBarStyles = {
    width: "100%",
    height: "20px",
    backgroundColor: "#f2f2f2",
    margin: "10px 0",
    borderRadius: "10px",
  };

  const colorStops = [
    { percent: 0, color: "red" },
    { percent: 25, color: "orange" },
    { percent: 50, color: "yellowgreen" },
    { percent: 75, color: "lightgreen" },
    { percent: 100, color: "green" },
  ];

  const findColor = () => {
    for (let i = 0; i < colorStops.length - 1; i++) {
      const start = colorStops[i];
      const end = colorStops[i + 1];
      if (progress >= start.percent && progress <= end.percent) {
        const percent =
          (progress - start.percent) / (end.percent - start.percent);
        const red = Math.round(start.color.slice(1, 3), 16) * (1 - percent) +
          Math.round(end.color.slice(1, 3), 16) * percent;
        const green = Math.round(start.color.slice(3, 5), 16) * (1 - percent) +
          Math.round(end.color.slice(3, 5), 16) * percent;
        const blue = Math.round(start.color.slice(5, 7), 16) * (1 - percent) +
          Math.round(end.color.slice(5, 7), 16) * percent;
        return `rgb(${red}, ${green}, ${blue})`;
      }
    }
    return colorStops[colorStops.length - 1].color;
  };

  const progressStyles = {
    height: "100%",
    lineHeight: "20px",
    textAlign: "center",
    borderRadius: "10px",
    width: `${progress}%`,
    backgroundColor: findColor(),
    color: "white",
  };

  return (
    <div style={progressBarStyles}>
      <div style={progressStyles}>{progress}%</div>
    </div>
  );
};

export default ProgressBar;
