import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import { useCookies } from "react-cookie";
import { Navbar, Footer } from "../../components";
import axios from "axios";
import logo from "../../components/templates/img/logo.png";

const Login = () => {
  const [loginForm, setLoginForm] = useState({ username: "", password: "" });
  const [error, setError] = useState("");
  const history = useHistory();
  const [setAuthToken] = useCookies(["authToken"]);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/login`, {
        username: loginForm.username,
        password: loginForm.password,
      });
      console.log(response);
      if (response.data.message === "Authenticated") {
        setAuthToken("authToken", response.data.token);
        history.push("/");
      } else {
        setError("Invalid username or password");
      }
    } catch (error) {
      console.log(error);
      setError("Server error");
    }
  };

  return (
    <>
      <Navbar />
      <div className="container">
        <div className="row mt-5">
          <div className="col-md-6">
            <h2>Login</h2>
            <LoginForm {...loginForm} onChange={setLoginForm} onSubmit={handleLogin} error={error} />
          </div>
          <div className="col-md-6 col-12">
            <img src={logo} alt="logo" className="float-right" height="300px"></img>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
};

const LoginForm = ({ username, password, onChange, onSubmit, error }) => {
  const handleChange = (e) => {
    setLoginForm({ ...loginForm, [e.target.name]: e.target.value });
  };
  

  return (
    <form onSubmit={onSubmit}>
      {error && <div className="alert alert-danger">{error}</div>}

      <div className="form-group">
        <label htmlFor="username">Username</label>
        <input type="text" className="form-control" id="username" name="username" placeholder="Enter username" value={username} onChange={handleChange} />
      </div>

      <div className="form-group">
        <label htmlFor="password">Password</label>
        <input type="password" className="form-control" id="password" name="password" placeholder="Password" value={password} onChange={handleChange} />
      </div>

      <button className="btn btn-primary" type="submit">
        Login
      </button>
    </form>
  );
};

export default Login;
